function update_item_data_and_dependencies(frm) {
    frm.doc.items_details.forEach(row => {
        if (!row.item_code) return;

        // Update Item Master
        frappe.call({
            method: "frappe.client.set_value",
            args: {
                doctype: "Item",
                name: row.item_code,
                fieldname: {
                    ...(row.customer_sku ? { custom_customer_sku: row.customer_sku } : {}),
                    ...(row.item_name ? { item_name: row.item_name } : {}),
                    ...(row.description ? { description: row.description } : {})
                }
            },
            callback: function () {
                console.log(`Item ${row.item_code} updated.`);

                // Update BOM
                if (row.bom) {
                frappe.call({
                    method: "frappe.client.get",
                    args: {
                        doctype: "Item",
                        name: row.item_code
                    },
                    callback: function(itemRes) {
                        const updated_item_name = itemRes.message.item_name;
                        const updated_description = itemRes.message.description;
                
                        bom.items.forEach(i => {
                            if (i.item_code === row.item_code) {
                                i.item_name = updated_item_name;
                                i.description = updated_description;
                            }
                        });
                
                        frappe.call({
                            method: "frappe.client.save",
                            args: { doc: bom },
                            callback: function () {
                                console.log(`BOM ${row.bom} synced with Item master values.`);
                            }
                        });
                    }
                });

                }

                // Update Purchase Order
                if (row.purchase_order) {
                    frappe.call({
                        method: "frappe.client.get",
                        args: {
                            doctype: "Purchase Order",
                            name: row.purchase_order
                        },
                        callback: function(poRes) {
                            const po = poRes.message;
                            let updated = false;
                
                            po.items.forEach(po_item => {
                                if (po_item.item_code === row.item_code) {
                                    po_item.description = row.description;
                                    updated = true;
                                }
                            });
                
                            if (updated) {
                                frappe.call({
                                    method: "frappe.client.save",
                                    args: { doc: po },
                                    callback: function () {
                                        console.log(`PO ${row.purchase_order} description updated.`);
                                    }
                                });
                            }
                        }
                    });
                }

            }
        });
    });
}

// ========== Fetch Additional Item Info ==========
function fetch_item_info(frm, cdt, cdn) {
    const row = locals[cdt][cdn];
    if (!row.item_code) return;

    frappe.call({
        method: "get_item_related_info",
        args: { item_code: row.item_code },
        callback: function (r) {
            if (r.message) {
                const info = r.message;

                const group = (info.item_group || "").toLowerCase();
                const isSpecialGroup = ["ring", "bracelet", "bangle"].includes(group);

                const finalQty = isSpecialGroup ? info.sales_order_total_qty : info.qty;
                const finalSize = isSpecialGroup ? info.size_count : 0;

                frappe.model.set_value(cdt, cdn, {
                    sales_order: info.sales_order,
                    delivery_date: info.delivery_date,
                    sales_order_status: info.sales_order_status,
                    purchase_order: info.purchase_order,
                    purchase_order_status: info.purchase_order_status,
                    bom: info.bom,
                    bom_status: info.bom_status,
                    item_group: info.item_group || "",
                    qty: finalQty,
                    size: finalSize,
                    rate: info.rate,
                    amount: info.amount,
                    customer_po_id: info.po_no,
                    customer_po_date: info.po_date

                    
                });

                setTimeout(() => {
                    frm.set_df_property("value_comparision", "hidden", 0);
                    update_value_comparison(frm);
                }, 300); 
            }
        }
    });
}



