frappe.ui.form.on('User Feedback', {
    refresh(frm) {
        calculate_age(frm);
        setInterval(() => {
            if (should_update_age(frm)) {
                calculate_age(frm);
            }
        }, 60000); 
    },
    time: calculate_age,
    date: calculate_age,
    status: calculate_age,
    feedback_type: calculate_age
});

function should_update_age(frm) {
    const status = frm.doc.status;
    const feedbackType = frm.doc.feedback_type;

    if (feedbackType === "Rating Only") return false;

    return status === "Open" || status === "In Progress";
}

function calculate_age(frm) {
    const feedbackType = frm.doc.feedback_type;

    if (feedbackType === "Rating Only") {
        frm.set_value("age", "");
        return;
    }

    if (!should_update_age(frm)) return;

    const date = frm.doc.date;
    const time = frm.doc.time;

    if (!date || !time) return;

    const datetime_str = `${date} ${time}`;
    const feedback_datetime = new Date(datetime_str);
    const now = new Date();

    const msDiff = now - feedback_datetime;
    if (msDiff < 0) {
        frm.set_value("age", "Future Time");
        return;
    }

    const minutes = msDiff / (1000 * 60);
    const hours = msDiff / (1000 * 60 * 60);
    const days = msDiff / (1000 * 60 * 60 * 24);
    const weeks = days / 7;
    const months = days / 30;
    const years = days / 365;

    let ageStr = "";

    if (minutes < 60) {
        ageStr = `${minutes.toFixed(1)} m`;
    } else if (hours < 24) {
        ageStr = `${hours.toFixed(1)} H`;
    } else if (days < 7) {
        ageStr = `${days.toFixed(1)} D`;
    } else if (days < 30) {
        ageStr = `${weeks.toFixed(1)} W`;
    } else if (days < 365) {
        ageStr = `${months.toFixed(1)} M`;
    } else {
        ageStr = `${years.toFixed(1)} Y`;
    }

    frm.set_value("age", ageStr);
}
